sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		//Create GlideDateTime objects for the current dateandthe passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//if the test date is before rightnow, return true, else return false
		if (testdate.before(rightnow)){
			return true;
		}else{
			return false;
		}
	},

	isDateToday: function(strDate){
		//Create GlideDateTime object for the current date and the passed in date. 
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//get only date portion of the date (no timestamp)
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();

		//compare thedates
		if (today.compareTo(istoday) == 0){
			return true;
		}else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
